mapper.select:
SELECT t_customer_return_list.`return_number`,t_customer_return_list.`return_date`,t_customer.`customer_name`,t_customer_return_list_goods.`goods_code`,t_customer_return_list_goods.`goods_name`,t_customer_return_list_goods.`goods_model`,t_goods_type.`goods_type_name`,t_customer_return_list_goods.`goods_unit`,t_customer_return_list_goods.`price`,t_customer_return_list_goods.`goods_num`,t_customer_return_list_goods.`total`
FROM t_customer_return_list_goods
INNER JOIN t_customer_return_list
ON t_customer_return_list_goods.`customer_return_list_id`=t_customer_return_list.`customer_return_list_id`
INNER JOIN t_customer
ON t_customer_return_list.`customer_id`=t_customer.`customer_id`
INNER JOIN t_goods_type
ON t_customer_return_list_goods.`goods_type_id`=t_goods_type.`goods_type_id`


        AND t_customer_return_list.return_date >= #{sTime}


        AND t_customer_return_list.return_date <= #{eTime}


        AND t_goods_type.goods_type_id = #{goodsTypeId}


        AND t_customer_return_list_goods.goods_name = #{codeOrName}
        or t_customer_return_list_goods.goods_code = #{codeOrName}
